import React, { Component } from "react";
import CartScrollBar from "../CartScrollBar/CartScrollBar";
import EmptyCart from "../EmptyStates/EmptyCart/EmptyCart";
import CSSTransitionGroup from "react-transition-group/CSSTransitionGroup";
import { findDOMNode } from "react-dom";
import argoslogo from '../../images/argoslogo.png'
import search from '../../images/search-small.png'
import back from '../../images/back.png';
import cart from '../../images/cart.png'
import './Header.scss';


class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showCart: false,
      cart: this.props.cartItems,
      mobileSearch: false
    };
  }
  handleCart(e) {
    e.preventDefault();
    this.setState({
      showCart: !this.state.showCart
    });
  }
  handleSubmit(e) {
    e.preventDefault();
  }
  handleMobileSearch(e) {
    e.preventDefault();
    this.setState({
      mobileSearch: true
    });
  }
  handleSearchNav(e) {
    e.preventDefault();
    this.setState(
      {
        mobileSearch: false
      },
      function() {
        this.refs.searchBox.value = "";
        this.props.handleMobileSearch();
      }
    );
  }
  handleClickOutside(event) {
    const cartNode = findDOMNode(this.refs.cartPreview);
    // const buttonNode = findDOMNode(this.refs.cartButton);
    if (cartNode.classList.contains("active")) {
      if (!cartNode || !cartNode.contains(event.target)) {
        this.setState({
          showCart: false
        });
        event.stopPropagation();
      }
    }
  }
  componentDidMount() {
    document.addEventListener(
      "click",
      this.handleClickOutside.bind(this),
      true
    );
  }
  componentWillUnmount() {
    document.removeEventListener(
      "click",
      this.handleClickOutside.bind(this),
      true
    );
  }
  render() {
    let cartItems;
    cartItems = this.state.cart.map(product => {
      console.log(product)
      return (
        <li className={`cart-item ${styles['header-container']}`} key={product.title}>
          <img className={`product-image ${styles['product-image']}`} src={product.image} alt={product.title} />
          <div className={`product-info ${styles['product-info']}`}>
            <p className={`product-name ${styles['product-name']}`}>{product.title}</p>
            <p className={`product-price ${styles['product-price']}`}>{product.price}</p>
          </div>
          <div className={`product-total ${styles['product-total']}`}>
            <p className={`quantity ${styles.quantity}`}>
              {product.quantity} {product.quantity > 1 ? "Nos." : "No."}{" "}
            </p>
            <p className={`amount ${styles.amount}`}>{product.quantity * product.price}</p>
          </div>
          <a
            className={`product-remove ${styles['product-remove']}`}
            href="/#"
            onClick={this.props.removeProduct.bind(this, product.id)}
          >
            ×
          </a>
        </li>
      );
    });
    let view;
    if (cartItems.length <= 0) {
      view = <EmptyCart />;
    } else {
      view = (
        <CSSTransitionGroup
          transitionName="fadeIn"
          transitionEnterTimeout={500}
          transitionLeaveTimeout={300}
          component="ul"
          className="cart-items"
        >
          {cartItems}
        </CSSTransitionGroup>
      );
    }
    return (
      <header>
        <div className={`container ${styles.container}`}>
          <div className={`brand ${styles.brand}`}>
            <img
              className={`logo ${styles.logo}`}
              src={argoslogo}
              alt="Argos Brand Logo"
            />
          </div>

          <div className={`search ${styles.search}`}>
            <a
              className={`mobile-search ${styles['mobile-search']}`}
              href="/#"
              onClick={this.handleMobileSearch.bind(this)}
            >
              <img
                src={search}
                alt="search"
              />
            </a>
            <form
              action="#"
              method="get"
              className={
                this.state.mobileSearch ? "search-form active" : "search-form"
              }
            >
              <a
                className={`back-button ${styles['back-button']}`}
                href="/#"
                onClick={this.handleSearchNav.bind(this)}
              >
                <img
                  src={back}
                  alt="back"
                />
              </a>
              <input
                type="search"
                ref="searchBox"
                placeholder="Search for products"
                className={`search-keyword ${styles['search-keyword']}`}
                onChange={this.props.handleSearch}
              />
              <button
                className={`search-button ${styles['search-button']}`}
                type="submit"
                onClick={this.handleSubmit.bind(this)}
              />
            </form>
          </div>

          <div className={`cart ${styles.cart}`}>
            <div className={`cart-info ${styles['cart-info']}`}>
              <table>
                <tbody>
                  <tr>
                    <td>No. of items</td>
                    <td>:</td>
                    <td>
                      <strong>{this.props.totalItems}</strong>
                    </td>
                  </tr>
                  <tr>
                    <td>Sub Total</td>
                    <td>:</td>
                    <td>
                      <strong>{this.props.total}</strong>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
            <a
              className={`cart-icon ${styles['cart-icon']}`}
              href="/#"
              onClick={this.handleCart.bind(this)}
              ref="cartButton"
            >
              <img
                className={this.props.cartBounce ? `shake ${styles.shake}` : " "}
                src={cart}
                alt="Cart"
              />
              {this.props.totalItems ? (
                <span className={`cart-count ${styles['cart-count']}`}>{this.props.totalItems}</span>
              ) : (
                ""
              )}
            </a>
            <div
              className={
                this.state.showCart ? "cart-preview active" : "cart-preview"
              }
              ref="cartPreview"
            >
              <CartScrollBar>{view}</CartScrollBar>
              <div className={`action-block ${styles['action-block']}`}>
                <button
                  type="button"
                  className={this.state.cart.length > 0 ? " " : "disabled"}
                >
                  PROCEED TO CHECKOUT
                </button>
              </div>
            </div>
          </div>
        </div>
      </header>
    );
  }
}

export default Header;
